module.exports = {
	log: function () {
		if (global.env.level == 'prod') {
			global.rollbar.log(arguments[0], arguments)
			console.log(...arguments)
		} else {
			console.log(...arguments)
		}
	},
	error: function () {
		if (global.env.level == 'prod') {
			global.rollbar.error(arguments[0], arguments)
			console.log(...arguments)
		} else {
			console.error(...arguments)
		}
	}
}