let fs = require('fs')
let path = require('path')
let scripts = []
let files = fs.readdirSync(path.join(__dirname+'/node_modules/'))
files.forEach((file)=>{
	scripts.push(
		require(file)
	)
})
module.exports = async function(args){
	var promises = []
	scripts.forEach(script=>{
		if(typeof script == 'function'){
			promises.push(script(args))
		}
	})
	Promise
	.all(promises)
	.then(()=>{
		return scripts
	})
	.catch(err=>{
		if(global.logging){
			console.error(err)
		}
	})
}