let fs = require('fs')
let path = require('path')
let Sites = []
let sites = fs.readdirSync(path.join(__dirname+'/node_modules/'))
sites.forEach((site)=>{
	Sites.push(
		require(site)
	)
})

Sites.schemas = function(args){
	let schemas = []
	Sites.forEach(site=>{
		let loop = Object.keys(site)
		loop.forEach(siteTarget=>{
			if(site[siteTarget].schema){
				schemas.push(site[siteTarget].schema)
			}
		})
	})
	return schemas
}

module.exports = Sites